from zad2 import ex2_op
# https://pl.wikipedia.org/wiki/Kryterium_Eulera

def ex4(b, p):
    result = ex2_op(b,
                    (p-1)//2,
                     p)

    if result == 1:
        #print(f"a - jest liczbą kwadratową (mod p)")
        return True
    else:
        #print("a - NIE jest resztą kwadratową (mod p)")
        return False


